mapper.select:
SELECT * FROM USER

	
		AND state = #{state}
	
	
		AND userName = #{userName}
	
	
		AND password = #{password}
	
	
		AND role = #{role}
	
	
		AND id = #{id}

mapper.update:
UPDATE USER
SET
	updateTime = #{updateTime}

	,password = #{password}


	,state = #{state}


	,role = #{role}


	,activateWay = #{activateWay}


	,activeCode = #{activeCode}

WHERE
	id = #{id}
